const filtradas = conversas.filter(item => {
  const identifier = item.meta?.sender?.identifier || '';
  const lastMessageDate = new Date(item.custom_attributes?.last_message);

  // A data da mensagem pode estar em formato ISO 8601 (string) ou timestamp (number).
  // A convers√£o para `Date` lida com ambos. Se a data for inv√°lida, a condi√ß√£o √© falsa.
  const isRecent = lastMessageDate.getTime() >= timestampLimite;

